    // This method closes all the frames except the main menu one
    private void closeAllFramesExceptCurrent() 
    {
        // Create an array of frames
        Frame[] frames = Frame.getFrames(); 

        // Use a for loop because is more easier to loop over
        for (Frame frame : frames) 
        {
            // For every frame if its an instance of Jframe and is not this one
            if (frame instanceof JFrame && frame != this) 
            {
                // We close that frame
                frame.dispose();
            }
        }
    }

    // This method runs when a button is pressed
    private void buttonAction()
    {
        // Stop the music + stop the song
        musicPlayer.stopSong();
        this.dispose();
    }

